targets.expr:
sum by (tunnel_id, direction, protocol) (
  rate(newt_tunnel_bytes_total{tunnel_id=~"$tunnel_id", direction=~"$direction", protocol=~"$protocol"}[5m])
)

{service_name="traefik"} 
| detected_level="info"
| line_format "{{.DownstreamStatus}} {{.RequestMethod}} {{.RequestHost}}{{.RequestPath}} | Duration: {{div .Duration 1000000}}ms (Origin: {{div .OriginDuration 1000000}}ms, Overhead: {{div .Overhead 1000000}}ms) | Client: {{.ClientHost}} {{if .request_Cf_Ipcountry}}({{.request_Cf_Ipcountry}}){{end}} | Service: {{.ServiceName}} | TraceID: {{.TraceId}}"

sum(
  rate(({service_name="traefik"} | json DownstreamContentSize | unwrap DownstreamContentSize)[1m])
) by (service_name)
